mapper.select:
SELECT * FROM ( SELECT l.id id, l.loading_no loading_no, l.plate_number plate_number, l.loading_date loading_date, l.create_time create_time, l.state state, (select min(d.state) from oms_bucket_goods_detail d where d.order_loading_id = l.id and d.state != 0) min_state, (select max(d.state) from oms_bucket_goods_detail d where d.order_loading_id = l.id and d.state != 0) max_state, (select sum(d.total_number) from oms_bucket_goods_detail d where d.order_loading_id = l.id and d.state != 0) total_number, (select sum(d.total_weight) from oms_bucket_goods_detail d where d.order_loading_id = l.id and d.state != 0) total_weight, (select sum(d.freight_fee) from oms_bucket_goods_detail d where d.order_loading_id = l.id and d.state != 0) freight_fee, b.id b_id, b.order_loading_id b_order_loading_id, b.plate_number b_plate_number, b.bucket_no b_bucket_no, b.export_company b_export_company, g.id b_g_id, g.local_cust b_g_local_cust, g.hk_cust b_g_hk_cust, g.order_loading_id b_g_order_loading_id, g.bucket_id b_g_bucket_id, g.plate_number b_g_plate_number, g.good_type b_g_good_type, g.packing_type b_g_packing_type, g.fcl_number b_g_fcl_number, g.unit_weight b_g_unit_weight, g.addition_number b_g_addition_number, g.addition_weight1 b_g_addition_weight1, g.addition_weight2 b_g_addition_weight2, g.total_number b_g_total_number, g.total_weight b_g_total_weight, g.freight_fee b_g_freight_fee, g.loading_confirm_state b_g_loading_confirm_state, g.sending_confirm_state b_g_sending_confirm_state, g.sending_comfirm_operator b_g_sending_comfirm_operator, g.sending_confirm_notes b_g_sending_confirm_notes, g.state b_g_state, g.update_time update_time, g.operator operator FROM oms_order_loading l left join oms_order_loading_bucket b on l.id = b.order_loading_id left join oms_bucket_goods_detail g on b.order_loading_id = g.order_loading_id and b.id = g.bucket_id WHERE 1=1
) t WHERE 1=1
AND t.min_state IN
#{state}
ORDER BY t.id DESC

SELECT c.total_weight - sum(IFNULL(d.total_weight, 0)) diff_weight,
       c.total_number - sum(IFNULL(d.total_number, 0)) diff_number
FROM oms_cust_order c
         LEFT JOIN oms_order_loading l
                   ON c.order_date = l.loading_date
         LEFT JOIN oms_bucket_goods_detail d
                   ON l.id = d.order_loading_id AND c.local_cust = d.local_cust AND c.hk_cust = d.hk_cust
                       AND c.good_type = d.good_type AND c.packing_type = d.packing_type
WHERE c.order_date = #{queryParam.loadingDate}
  AND c.local_cust = #{queryParam.localCust}
  AND c.hk_cust = #{queryParam.hkCust}
  AND c.good_type = #{queryParam.goodType}
  AND c.packing_type = #{queryParam.packingType}
GROUP BY c.order_date, c.local_cust, c.hk_cust, c.good_type, c.packing_type;
